import React from 'react';
import ReactDOM from 'react-dom';

class Timer extends React.Component {
  constructor(props) {
    super(props);
    this.state = {countTime: this.props.startValue};
  }

  componentDidMount() {
    this.timerID = setInterval(
      () => this.setState(prevState => ({
        countTime: prevState.countTime -1
      })
    ), 1000);
  }

  componentWillUnmount() {
    // the clock is no longer being displayed so kill the timer
    clearInterval(this.timerID);
  }

  render() {
    if(this.state.countTime > 0){
      return <h2>Time remaining: {this.state.countTime} seconds.</h2>;
    } else {
      // the clock is no longer being displayed so kill the timer
      clearInterval(this.timerID);
      return <h2>Time up!!</h2>;
    }
  }
}

ReactDOM.render(
  <div>
    <Timer startValue={10}/>
    <Timer startValue={30}/>
    <Timer startValue={60}/>
  </div>,
  document.getElementById('root')
);
